import React from 'react';
import { Box } from '@chakra-ui/core';

interface WrapperProps {
    variant: 'small' | 'regular';
}

export const Wrapper: React.FC<WrapperProps> = ({ children, variant }) => {
    return (
        <Box
            mt={8}
            mx='auto'
            maxW={variant === 'regular' ? '800px' : '400px'}
            width='100%'
        >
            {children}
        </Box>
    );
};
